import matplotlib.pyplot as plt

def plot_equity_curve(equity):
    plt.plot(equity)
    plt.title("Р В Р Р‹Р В РІР‚С™Р В Р Р‹Р РЋРЎСџР В Р вЂ Р В РІР‚С™Р РЋРЎв„ўР В Р вЂ Р Р†Р вЂљРЎв„ўР вЂ™Р’В¬ Equity Curve")
    plt.xlabel("Trade #")
    plt.ylabel("Balance")
    plt.grid(True)
    plt.show()

def print_trade_summary(trades):
    for i, t in enumerate(trades):
        print(f"{i+1:02d}) {t['side']} {t['symbol']} @ {t['entry']} Р В Р’В Р В РІР‚В Р В Р вЂ Р В РІР‚С™Р вЂ™Р’В Р В Р вЂ Р В РІР‚С™Р Р†РІР‚С›РЎС› {t['target']} / SL {t['stop']}")

